void printBinary(int num) {
	    int bits = sizeof(num) * 8;  // Number of bits in an integer
	        for (int i = bits - 1; i >= 0; i--) {
			        int bit = (num >> i) & 1;  // Extract the i-th bit
				        printf("%d", bit);
					    }
}

